        //         placeholder="" onChange={(e) => handleChange(index, e)}
        //         name="back"
        //     />
        // </div>]
        console.log("hi");
        setCardValues([...cardValues,{
            front:"",
            back:"",
            starred:false,
            score:0
        }]);
        console.log("CV:"+cardValues.length);
        console.log("LI:"+listItems.length);
    }

    return (<>
    <ul>{listItems}</ul>
    <button onClick={() => addCard()}>Add Card</button>
    </>)
}

export default Deck;
