import {useState, useEffect} from "react";
import Axios from "axios";

function Deck(props) {
    // const initCardValues = {
    //     front:["","","","",""],
    //     back:["","","","",""],
    //     starred:[false,false,false,false,false],
    //     score:[0,0,0,0,0]
    // }
    const initCardValues = props.numbers.map(element => 
        ({
            front:"",
            back:"",
            starred:false,
            score:0
        })

    );

    const [cardValues,setCardValues] = useState([initCardValues]);
    const listItems = props.numbers.map((number) =>
        <div key={number}>
            {number}
            <input className={"front-input-"+number.toString}
                placeholder="" onChange={(e) => handleChange({number}, e)}
                name="front"
            />

            <input className={"back-input-"+number.toString}
                placeholder="" onChange={(e) => handleChange({number}, e)}
                name="back"
            />
        </div>
    );

    function handleChange(num,e) {
        const {name,value} = e.target;
        setCardValues({
            cardValues: [
                ...cardValues.slice(0,num),
                {
                    ...cardValues[1],
                    name: value,
                },
                ...cardValues.slice(num<cardValues.length-1 ? num+1 : num)
            ]
          });
        console.log(cardValues);
    }


    return <ul>{listItems}</ul>
}

export default Deck;
